"use strict";(self.webpackChunkdocu_scilla=self.webpackChunkdocu_scilla||[]).push([[4476],{8810:function(e){e.exports=JSON.parse('{"name":"fungible","permalink":"/Scilla-Cookbook-1/tags/fungible","docs":[{"id":"recipes/scilla-recipes/fungible","title":"Fungible token","description":"Fungible tokens are an open standard for creating currencies. Fungibility is the property of goods or commodities whose individual units are essentially interchangeable, and each of its parts is indistinguishable from another part. An example is 1 ZIL can be traded for an equal value of 1 ZIL.","permalink":"/Scilla-Cookbook-1/recipes/scilla-recipes/fungible"},{"id":"recipes/scilla-recipes/nonfungible","title":"Non-fungible token","description":"A fungible token is unit of currency that can be readily interchanged, it has properties like fiat where 1 unit of currency is equal to 1 unit of the same currency.","permalink":"/Scilla-Cookbook-1/recipes/scilla-recipes/nonfungible"}],"allTagsPath":"/Scilla-Cookbook-1/tags"}')}}]);